def shift_word(word):
        shifted_letter = ""
        for letter in word:
            letter_idx = ord(letter) + 2
            if letter_idx > ord('z'):
                letter_idx -= 26
            shifted_letter += chr(letter_idx)
        return shifted_letter

print(shift_word('apple'))

def factorial(num):
    fact = 1

    for i in range (1, num+1):
        fact = fact * i

    return fact

print("The factorial of", num, "is", factorial(num))

